      for( var nJ = 0, nK = 0, tmp = this.m_nPointChunk; 0 <= tmp ? nK < tmp : nK > tmp; nJ = 0 <= tmp ? ++nK : --nK )
         this.m_vBufs.push( new Float32Array( this.m_vBuf.buffer, 0, nJ * this.m_nVertexSize * 6 ) );

